        //this.appComponent.handleMessage(JSON.stringify(message.body));

            const parsedMessage = JSON.parse(message);
            this.appComponent.handleMessage(parsedMessage.body);
          
          
    }*/
    onMessageReceived(message: any) {
        console.log("Message Received from Server :: ", message);
      
        // Check if the message contains the "content" property with JSON data
        if (typeof message === "object" && message.hasOwnProperty("content")) {
          const jsonData = message.content;
      
          // Parse the JSON data
          try {
            const parsedMessage = JSON.parse(jsonData);
            // Now you can access properties of the parsed message and handle it accordingly
            console.log("Parsed Message:", parsedMessage);
            this.appComponent.handleMessage(parsedMessage.content);
          } catch (error) {
            console.error("Error parsing message:", error);
          }
        } else {
          console.error("Invalid message format:", message);
        }
      }
      
      
      
      
      
}
