import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { LoginService } from 'src/app/services/login.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  type: string = "password";
  isText: boolean = false;
  eyeIcon: string = "fa-eye-slash";

  userMail: string = "";
  userPassword: string = "";

  constructor(private router: Router, private loginService: LoginService) { }

  ngOnInit(): void { }

  hideShowPass() {
    this.isText = !this.isText;
    this.isText ? this.eyeIcon = "fa-eye" : this.eyeIcon = "fa-eye-slash";
    this.isText ? this.type = "text" : this.type = "password";
  }

  login(loginForm: FormGroup) {
    if (loginForm.valid) {
      const authenticationRequest = {
        username: this.userMail,
        password: this.userPassword
      };

      this.loginService.authenticate(authenticationRequest).subscribe(
        (response) => {
          // Save the token in local storage
          localStorage.setItem('token', response.token);
          // Redirect to the dashboard or desired page
          this.router.navigate(['/dashboard']);
        },
        (error) => {
          console.error('Error during authentication:', error);
          alert('Invalid username or password');
        }
      );
    }
  }
}
<div class="outer">
    <div class="container">
        <div class="row">
            <div class="offset-md-2 col-lg-5 col-md-7 offset-lg-4 offset-md-3">
                <div class="panel border bg-white">
                    <div class="panel-heading">
                        <h3 class="pt-3 font-weight-bold">Login</h3>
                    </div>
                    <div class="panel-body p-3">
                        <form #loginForm="ngForm" (submit)="login(loginForm)">
                            <div class="form-group pt-3">
                                <div class="input-field">
                                    <span class="fa fa-user p-2"></span>
                                    <input type="email" [(ngModel)]="userMail" name="userMail" required
                                        aria-describedby="emailHelp" class="form-control" placeholder="Email">
                                </div>
                            </div>

                            <div class="form-group pt-3">
                                <div class="input-field">
                                    <i class="fa fa-lock p-2"></i>
                                    <input [type]="type" type="password" [(ngModel)]="userPassword" name="userPassword"
                                        required class="form-control" placeholder="Password">
                                </div>
                            </div>

                            <div class="form-inline fr mt-4 w-100">
                                <a href="" class="font-weight-bold" id="forget">Forget Password?</a>
                            </div>

                            <button type="submit" class="btn btn-primary btn-block mt-5">
                                <span class="fa fa-lock"></span> Login
                            </button>
                            <div class="text-center pt-4 text-muted">Don't have an account? <a
                                    routerLink="/register" id="sign">Sign Up</a></div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
