import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthData, AuthenticationResponse, LoginService } from 'src/app/services/login.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{
  type: string = "password";
  isText: boolean = false;
  eyeIcon:string = "fa-eye-slash";

  userMail: string="";
  userPassword: string="";

  constructor(private router: Router, private http: HttpClient, private service: LoginService){}

  ngOnInit():void{

  }

  hideShowPass(){
    this.isText = !this.isText;
    this.isText? this.eyeIcon = "fa-eye" : this.eyeIcon = "fa-eye-slash";
    this.isText ? this.type = "text": this.type = "password";
  }

  save(loginForm: NgForm) {
    const authData: AuthData = {
      userMail: this.userMail,
      userPassword: this.userPassword
    };

    this.service.login(loginForm.value).subscribe(
      (response: any) => {
        console.log(response);

        if (response.message === "Email not exists") {
          alert("Email not exists");
        } else if (response.message === "Login Success") {
          // Save the token in local storage (assuming response contains the token)
          localStorage.setItem('token', response.token);
          this.router.navigateByUrl('/register'); // Navigate to the desired page after successful login
        } else {
          alert("Incorrect email or password");
        }
      },
      (error) => {
        console.error('Error authenticating user:', error);
        alert('An error occurred during authentication');
      }
    );
  }

}
<div class="outer">
    <div class="container">
        <div class="row">
            <div class="offset-md-2 col-lg-5 col-md-7 offset-lg-4 offset-md-3">
                <div class="panel border bg-white">
                    <div class="panel-heading">
                        <h3 class="pt-3 font-weight-bold">Login</h3>
                    </div>
                    <div class="panel-body p-3">
                        <form>
                            <div class="form-group pt-3">
                                <div class="input-field">
                                    <span class="fa fa-user p-2"></span>
                                    <input type="email" [(ngModel)]="userMail" [ngModelOptions]="{standalone: true}" aria-describedby="emailHelp" class="form-control" placeholder="Email">
                                </div>
                            </div>

                            <div class="form-group pt-3">
                                <div class="input-field">
                                    <i class="fa fa-lock p-2"></i>
                                    <input [type]="type" type="password" [(ngModel)]="userPassword" [ngModelOptions]="{standalone: true}" class="form-control" placeholder="Password">
                                    <span (click)="hideShowPass()" class="fa fa-eye-slash"></span>
                                </div>
                            </div>

                            <div class="form-inline fr mt-4 w-100">
                                <a href="" class="font-weight-bold" id="forget">Forget Password?</a>
                            </div>

                            <button type="submit" (click)="save()" class="btn btn-primary btn-block mt-5"> <span class="fa fa-lock">  </span>   Login </button>
                            <button type="submit" class="btn btn-danger btn-block mt-5"> <span class="fa fa-google"></span> Google</button>
                            <div class="text-center pt-4 text-muted">Don't have account ? <a routerLink="/register" id="sign">Sign Up</a></div>
                        </form>
                    </div>


                </div>
            </div>
        </div>
    </div>
</div>
