import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent  implements OnInit{
  type: string = "password";
  isText: boolean = false;
  eyeIcon:string = "fa-eye-slash";

  userName: string="";
  userMail: string="";
  userPassword: string="";

  constructor(private http: HttpClient){}
  ngOnInit():void{

  }

  hideShowPass(){
    this.isText = !this.isText;
    this.isText? this.eyeIcon = "fa-eye" : this.eyeIcon = "fa-eye-slash";
    this.isText ? this.type = "text": this.type = "password";
  }

  save(){
    let bodyData = {

      "userMail": this.userMail,
      "userName": this.userName,
      "userPassword": this.userPassword
    };
    this.http.post("http://localhost:8080/api/auth/register",bodyData,{responseType: 'text'}).subscribe((resultData: any)=>
    {
      console.log(resultData);
      alert("Hmd User registered successfully");

    });
  }

}

<div class="outer">
    <div class="container">
        <div class="row">
            <div class="offset-md-2 col-lg-5 col-md-7 offset-lg-4 offset-md-3">
                <div class="panel border bg-white">
                    <div class="panel-heading">
                        <h3 class="pt-3 font-weight-bold">Sign up</h3>
                    </div>
                    <div class="panel-body p-3">
                        <form>
                            <div class="form-group pt-3">
                                <div class="input-field">
                                    <span class="fa fa-user p-2"></span>
                                    <input type="text" [(ngModel)]="userName" [ngModelOptions]="{standalone: true}" class="form-control" placeholder="Username">
                                </div>
                            </div>

                            <div class="form-group pt-3">
                                <div class="input-field">
                                    <span class="fa fa-envelope p-2"></span>
                                    <input type="email" [(ngModel)]="userMail" [ngModelOptions]="{standalone: true}" class="form-control" placeholder="Email">
                                </div>
                            </div>

                            <div class="form-group pt-3">
                                <div class="input-field">
                                    <i class="fa fa-lock p-2"></i>
                                    <input [type]="type" type="password" [(ngModel)]="userPassword" [ngModelOptions]="{standalone: true}" class="form-control" placeholder="Password">
                                    <span (click)="hideShowPass()"  class="fa fa-eye-slash"></span>
                                </div>
                            </div>



                            <button type="submit" (click)="save()" class="btn btn-primary btn-block mt-5"> <span class="fa fa-lock">  </span>   Sign up </button>
                            <div class="text-center pt-4 text-muted">Already have account ? <a routerLink="/login" id="login">Login</a></div>
                        </form>
                    </div>


                </div>
            </div>
        </div>
    </div>
</div>
