import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthData, LoginService } from 'src/app/services/login.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  type: string = "password";
  isText: boolean = false;
  eyeIcon: string = "fa-eye-slash";

  userMail: string = "";
  userPassword: string = "";
  loginPayload: AuthData | undefined;
  /////
  loginForm: FormGroup;
  siteKey: string = "6LfltwkbAAAAAEG_xuXSPuAtxgxxvKi_jFxaNcjN";

  localpattern = "[^[a-zA-Z0-9]{6,16}$]";

  isSubmitted = false;

  constructor(private formBuilder: FormBuilder, private authService: LoginService, private router: Router) {
    this.loginForm = this.formBuilder.group({
      userMail: ['', [Validators.required, Validators.email]],
      userPassword: ['', [Validators.required]]
    });
  }

  ngOnInit(): void { }

  get mail() { return this.loginForm.get('userMail'); }
  get password() { return this.loginForm.get('userPassword'); }

  onSubmit() {
    this.loginPayload = {
      userMail: this.userMail,
      userPassword: this.userPassword
    };

    // Negate the condition here
    this.authService.loginyes(this.loginPayload).subscribe(data => {
      console.log("I'am server");
      if (data) {
        console.log(data);
        this.router.navigateByUrl(`/`);
      } else {
        console.log(data);
      }
    });
  }

  // ... Rest of the code (hideShowPass and login methods) ...
}
