import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{
  type: string = "password";
  isText: boolean = false;
  eyeIcon:string = "fa-eye-slash";
  constructor(){}
  ngOnInit():void{

  }

  hideShowPass(){
    this.isText = !this.isText;
    this.isText? this.eyeIcon = "fa-eye" : this.eyeIcon = "fa-eye-slash";
    this.isText ? this.type = "text": this.type = "password";
  }

}
<div class="outer">
    <div class="container">
        <div class="row">
            <div class="offset-md-2 col-lg-5 col-md-7 offset-lg-4 offset-md-3">
                <div class="panel border bg-white">
                    <div class="panel-heading">
                        <h3 class="pt-3 font-weight-bold">Login</h3>
                    </div>
                    <div class="panel-body p-3">
                        <form>
                            <div class="form-group pt-3">
                                <div class="input-field">
                                    <span class="fa fa-user p-2"></span>
                                    <input type="text" placeholder="Username">
                                </div>
                            </div>

                            <div class="form-group pt-3">
                                <div class="input-field">
                                    <i class="fa fa-lock p-2"></i>
                                    <input [type]="type" type="text" placeholder="Password">
                                    <span (click)="hideShowPass()" class="fa fa-eye-slash"></span>
                                </div>
                            </div>

                            <div class="form-inline fr mt-4 w-100">
                                <a href="" class="font-weight-bold" id="forget">Forget Password?</a>
                            </div>

                            <button type="submit" class="btn btn-primary btn-block mt-5"> <span class="fa fa-lock"></span> Login</button>
                            <button type="submit" class="btn btn-danger btn-block mt-5"> <span class="fa fa-google"></span> Google</button>
                            <div class="text-center pt-4 text-muted">Don't have account ? <a routerLink="/signup" id="sign">Sign Up</a></div>
                        </form>
                    </div>


                </div>
            </div>
        </div>
    </div>
</div>
