import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';

export interface RegisterData {
  userName: string;
  userMail: string;
  userPassword: string;
}

export interface AuthData {
  userMail: string;
  userPassword: string;
}

export interface AuthenticationResponse {
  token: string;
  message: string;
  // Other properties if needed
}

@Injectable({
  providedIn: 'root'
})
export class LoginService {
  private apiUrl = 'http://localhost:8080/api/auth';
  requestHeader = new HttpHeaders({ 'No-Auth': 'True' });

  constructor(private http: HttpClient) {}

  public login(bodyData) {
    return this.http.post(this.apiUrl + '/authenticate', loginData, {
      headers: this.requestHeader,
    });
  }

  register(registerData: RegisterData): Observable<AuthenticationResponse> {
    const registerUrl = `${this.apiUrl}/register`;
    return this.http.post<AuthenticationResponse>(registerUrl, registerData);
  }

  authenticate(authData: AuthData): Observable<AuthenticationResponse> {
    const loginUrl = `${this.apiUrl}/login`;
    return this.http.post<AuthenticationResponse>(loginUrl, authData);
  }
}
