// login.component.ts
import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms'; // Import NgForm

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  userMail: string = "";
  userPassword: string = "";

  constructor() { }

  ngOnInit(): void { }

  login(loginForm: NgForm) {
    // Check if the form is valid
    if (loginForm.valid) {
      // Here you can save the user login information or perform other login-related actions
      // For example, you can send the login credentials to the server for authentication
      console.log("User Email: ", this.userMail);
      console.log("User Password: ", this.userPassword);

      // Reset the form after login
      loginForm.reset();
    }
  }

  // ... Rest of the code (hideShowPass method) ...
}
