// login.component.ts
import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms'; // Import NgForm

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  userMail: string = "";
  userPassword: string = "";

  constructor() { }

  ngOnInit(): void { }

  login(loginForm: NgForm) {
    // Check if the form is valid
    if (loginForm.valid) {
      // Here you can save the user login information or perform other login-related actions
      // For example, you can send the login credentials to the server for authentication
      console.log("User Email: ", this.userMail);
      console.log("User Password: ", this.userPassword);

      // Reset the form after login
      loginForm.reset();
    }
  }

  // ... Rest of the code (hideShowPass method) ...
}
<!-- login.component.html -->
<div class="outer">
    <!-- Rest of the HTML code as provided -->
    <form #loginForm="ngForm">
        <!-- Email input field -->
        <div class="form-group pt-3">
            <div class="input-field">
                <span class="fa fa-user p-2"></span>
                <input type="email" [(ngModel)]="userMail" name="userMail" aria-describedby="emailHelp" class="form-control" placeholder="Email" required>
            </div>
        </div>

        <!-- Password input field -->
        <div class="form-group pt-3">
            <div class="input-field">
                <i class="fa fa-lock p-2"></i>
                <input [(ngModel)]="userPassword" name="userPassword" class="form-control" placeholder="Password" required>
            </div>
        </div>

        <!-- Rest of the HTML code as provided -->

        <button type="submit" (click)="login(loginForm)" class="btn btn-primary btn-block mt-5">
            <span class="fa fa-lock"></span> Login
        </button>
        <div class="text-center pt-4 text-muted">Don't have an account? <a routerLink="/register" id="sign">Sign Up</a></div>
    </form>
</div>
