import { Component } from '@angular/core';
import { WebSocketAPI } from './services/WebSocketAPI';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  title = 'coChat';

  webSocketAPI!: WebSocketAPI;
  greeting: any;
  name!: string ;
  messages: { sender: string, receiver: string, content: string }[] = [];

  ngOnInit() {
    this.webSocketAPI = new WebSocketAPI(this); // Pass the current instance of AppComponent
  }

  connect(){
    this.webSocketAPI._connect();
  }

  disconnect(){
    this.webSocketAPI._disconnect();
  }

  sendMessage(){
    this.webSocketAPI._send(this.name);
    // Add the sent message to the messages list
    this.messages.push({ sender: this.name, receiver: 'Server', content: this.name });
  }

  handleMessage(message: any){
    console.log("Message Received from Server :: ", message);

    if (typeof message === "string") {
      // Parse the JSON data
      try {
        const parsedMessage = JSON.parse(message);
        // Now you can access properties of the parsed message and handle it accordingly
        console.log("Parsed Message:", parsedMessage);
        this.messages.push({ sender: parsedMessage.sender, receiver: this.name, content: parsedMessage.content });
      } catch (error) {
        console.error("Error parsing message:", error);
      }
    } else {
      console.error("Invalid message format:", message);
    }
  }
}
