import { HttpHeaders } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, NgForm, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthData, AuthenticationResponse, LoginService, RegisterData } from 'src/app/services/login.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{
  type: string = "password";
  isText: boolean = false;
  eyeIcon:string = "fa-eye-slash";

  userMail: string="";
  userPassword: string="";

  /////
  loginForm: FormGroup;
  loginPayload: AuthData;
  siteKey: string = "6LfltwkbAAAAAEG_xuXSPuAtxgxxvKi_jFxaNcjN";

  localpattern = "[^[a-zA-Z0-9]{6,16}$]";
  user: RegisterData;
  isSubmitted = false;

  constructor(private formBuilder: FormBuilder, private authService: LoginService, private service: LoginService,private router:Router,
    ) {
   this.siteKey = '6LfltwkbAAAAAEG_xuXSPuAtxgxxvKi_jFxaNcjN';
  this.siteKey ='6LeVa84aAAAAAB4myWaDKDK5DJptQQjzSq_Ix2NP';

  this.loginForm = new FormGroup({
    email: new FormControl('', [
      Validators.required,
      Validators.email
    ]),
    password: new FormControl('', [
      Validators.required,
      Validators.minLength(6),
      Validators.maxLength(64),

    ])
  });
  this.loginPayload = {
    email: '',
    password: ''
  };
  }
  ngOnInit():void{

  }
  get email(){return this.loginForm.get('email')}
  get password(){return this.loginForm.get('password')}
  onSubmit(){
    this.loginPayload.email = this.loginForm.get('email').value;
    this.loginPayload.password = this.loginForm.get('password').value;
    if (this.loginForm.invalid){
      alert('Votre formulaire est invalide.');
    }else{
    this.authService.login(this.loginPayload).subscribe( data => {
      if(data) {
        console.log("login success");
        this.router.navigateByUrl(`/`);
      } else {
        console.log("login failed")
      }
    });
    }


  }
  hideShowPass(){
    this.isText = !this.isText;
    this.isText? this.eyeIcon = "fa-eye" : this.eyeIcon = "fa-eye-slash";
    this.isText ? this.type = "text": this.type = "password";
  }


  login(loginForm: NgForm) {
    const authData: AuthData = {
      userMail: this.userMail,
      userPassword: this.userPassword
    };

    this.service.login(authData).subscribe(
      (response: AuthenticationResponse) => {
        console.log(response);

        if (response.message === "Email not exists") {
          alert("Email not exists");
        } else if (response.message === "Login Success") {
          // Save the token in localStorage and sessionStorage
          localStorage.setItem('token', response.token);
          sessionStorage.setItem('token', response.token);

          // You can also store other user-related data if you receive them in the response
          // For example, to store the user's email:
          localStorage.setItem('userEmail', authData.userMail);
          sessionStorage.setItem('userEmail', authData.userMail);

          this.router.navigateByUrl('/register'); // Navigate to the desired page after successful login
        } else {
          alert("Incorrect email or password");
        }
      },
      (error) => {
        console.error('Error authenticating user:', error);
        alert('An error occurred during authentication');
      }
    );
  }
}
